mapper.update:
update orders
set status = 3
where id = #{id};

update orders
set status=5
where id = #{id}

mapper.delete:
update orders
set cancel_reason = #{cancelReason}
where id = #{id};

mapper.select:
select count(*)
from orders
where status = #{confirmed}
   or status = #{deliveryInProgress}
   or status = #{toBeConfirmed}

select number, status, user_id, address_book_id, order_time, checkout_time, pay_method, pay_status, amount,
remark, phone, address, user_name, consignee, cancel_reason, rejection_reason, cancel_time,
estimated_delivery_time, delivery_status, delivery_time, pack_amount, tableware_number, tableware_status
from orders

    and number = #{number}
    and status = #{status}
    and order_time between #{beginTime} and #{endTime}

select count(id)
from orders

    and order_time > #{begin}
    and order_time < #{end}
    and status=#{status}

select sum(amount)
from orders

    and order_time > #{begin}
    and order_time < #{end}
    and status=#{status}

select od.name,sum(od.number) as number from order_detail od,orders o where od.order_id=o.id and o.status=5
and o.order_time > #{beginTime}
and o.order_time < #{endTime}
group by od.name order by number desc limit 0,10
