mapper.update:
update setmeal
set status = #{status}
where id=#{id}

update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image},
where id = #{id}

mapper.delete:
delete from setmeal
where id in

    #{id}

delete from setmeal_dish
where setmeal_dish.setmeal_id in

    #{id}

mapper.select:
select s.*,c.*
from setmeal s left join category c on c.id = s.category_id

    and s.name like concat('%',#{name},'%')
    and categoryId = #{categoryId}
    and s.status = #{status}

select count(id)
from setmeal

    and status=#{status}
    and category_id=#{categoryID}

select id, setmeal_id, dish_id, name, price, copies
from setmeal_dish
where setmeal_id = #{setmealId}

select count(id)
from setmeal
where id in

    #{id}

and status = #{status}
